import { useState  } from "react";
import { useSearchParams } from "react-router-dom";
import {createBlogDocument , getPostById } from '../../utils/firebase/firebase.utils';
import FormInput from "../form-input/form-input.component";
import FormText from "../form-input/form-text.component";
import Button from "../button/button.component";

const defaultFormFields= {
    title: '',
    body:'',

};
const AddBlog = () => {
    
    const [ formFields , SetFormFields ] = useState(defaultFormFields);
    const { title, body } = formFields;

    let [searchParams, setSearchParams] = useSearchParams()
    const editId = searchParams.get("type")
    console.log(editId);
    if(editId){
        const postData= async () =>{
            const response= await getPostById(editId);
            console.log(response);
            SetFormFields(response);
            return false;
            
        } 
        //postData();
    }
       
    
    //getBlogData();
    const resetFormFields = () =>{
        SetFormFields(defaultFormFields);
    }
    const handleSubmit = async (event) =>{
        event.preventDefault();
        const response = await createBlogDocument(formFields);
        // setContactMail(response);
        resetFormFields();
  
    }
    const handleChange = (event) =>{
        const {name, value } = event.target;
        SetFormFields({ ...formFields, [name]: value});
    };



    return (
        <div className="container">
       
            <span>Writ your Blog Here </span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label="Blog Title"
                    type="text" 
                    required 
                    onChange={handleChange}
                    name="title"
                    value={title}
                />
                <FormText
                    label="Blog Body" 
                    type="text" 
                    rows="10"
                    cols="150"
                    required 
                    onChange={handleChange} 
                    name="body" 
                    value={body}
                />
                
                
                <Button type="submit">Add Blog</Button>
            </form>
            </div>
    );

}

export default AddBlog;
