import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";


const AddBlog = () => {
    const [ formFields , SetFormFields ] = useState(defaultFormFields);
    const { name, email, phone, subject, message } = formFields;
    const [contactMail, setContactMail] = useState('');

    const [contactList, setContactList] = useState([]);
    const getContactData = async () =>{
        const resultData= await getAllContactMessage();
        console.log(resultData);
        const data = resultData.docs.map(doc => ({
            ...doc.data(),
            id: doc.id,
          }));
          setContactList(data);
    }
    const resetFormFields = () =>{
        SetFormFields(defaultFormFields);
    }
    const handleSubmit = async (event) =>{
        event.preventDefault();
        const response = await createContactDocument(formFields);
        setContactMail(response);
        resetFormFields();
  
    }
    const handleChange = (event) =>{
        const {name, value } = event.target;
        SetFormFields({ ...formFields, [name]: value});
    };



    return (
        <div className="container">
       
            <span>Writ your Blog Here </span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label="Blog Title"
                    type="text" 
                    required 
                    onChange={handleChange}
                    name="name"
                    value={name}
                />
                <textarea  name="content" type="text" placeholder="write yoyr content here" rows="10" cols="150" 
                onChange={handleChange} required >
            </textarea>
                <FormInput 
                    label="Body" 
                    type="text" 
                    required 
                    onChange={handleChange} 
                    name="content" 
                    value={content}
                />
                
                
                <Button type="submit">Add Blog</Button>
            </form>
            </div>
    );

}

export default AddBlog;
