    // //getPostData();
    const deletePost =async  (e) =>{
        const deleteOperation= await delteBlogPost(e.target.id);
        //getPostData();
    }
    return(
        <div className="container">
            <h2>Blog Page</h2>
            <Link className="nav-link" to='/add-blog'>Add Blog</Link>
            <div className="db-contact-data"><h2 className="w-full text-center font-bold text-xl">My Blog Post</h2>
                <table stripped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th width="270"> Id</th>
                            <th width="270"> Title</th>
                            <th width="370">Body</th>
                            <th width="370">Action</th>
                    
                        </tr>
                    </thead>
                    <tbody>     
                        {blogPost.map(posts=> (
                            
                            <tr key={posts.id}>
                                <td  width="350"> {posts.id}</td>
                                <td  width="350"> {posts.title}</td>
                                <td> {posts.body}</td>
                                <td>
                                    <Button type="button" id={posts.id}  onClick= {deletePost}>Delete</Button>
                                    <Button type="button" id={posts.id}  onClick={() => navigate(`/add-blog/?type=${posts.id}`)}>Edit</Button>
                                </td>
                            </tr>
                               
                            ))}
                    </tbody>
                </table>
                </div>
        </div>
        
    ) 
}

export default BlogPage;
