import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";


const AddBlog = () => {
    



    return (
        <div className="container">
       
            <span>Writ your Blog Here </span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label="Blog Title"
                    type="text" 
                    required 
                    onChange={handleChange}
                    name="name"
                    value={name}
                />
                <textarea  name="content" type="text" placeholder="write yoyr content here" rows="10" cols="150" 
                onChange={handleChange} required >
            </textarea>
                <FormInput 
                    label="Body" 
                    type="text" 
                    required 
                    onChange={handleChange} 
                    name="content" 
                    value={content}
                />
                
                
                <Button type="submit">Add Blog</Button>
            </form>
            </div>
    );

}

export default AddBlog;
