import { useEffect, useState } from "react";
import Button from "../button/button.component";
import { getAllBlogPost , delteBlogPost } from "../../utils/firebase/firebase.utils";
import { Link, useNavigate } from 'react-router-dom';
import './blogPage.style.scss';
const BlogPage= () =>{
    const navigate = useNavigate();
    const [blogPost , setBlogPost] = useState([]);
    let counter= 1;

    useEffect( ()=>{
        (async () => {
            try{
                const resultData= await getAllBlogPost();
                const data = resultData.docs.map(post => ({
                    ...post.data(),
                    id:post.id
                }))
                setBlogPost(data);
                console.log('hit');
            }catch(error){
                return false;
            }
        })();
    },[]);

    
   
    const deletePost =async  (e) =>{
        const deleteOperation= await delteBlogPost(e.target.id);
        (async () => {
            try{
                const resultData= await getAllBlogPost();
                const data = resultData.docs.map(post => ({
                    ...post.data(),
                    id:post.id
                }))
                setBlogPost(data);
                console.log('hit');
            }catch(error){
                return false;
            }
        })();
    }
    return(
        <div className="container">
            <h2>Blog Page</h2>
            <p className=""  onClick={() => navigate('/add-blog')}>Add Blog</p>
            <div className="blog-data">
            <p className="heading">My Blog Post</p>
                
                {blogPost.map(posts=> (
                    
                    <div key={posts.id}>
                        <div className="title"> {posts.title}</div>
                        <div className="body"> {posts.body}
                            <i className="fa fa-spinner fa-spin" id={posts.id} onClick={() => navigate(`/add-blog/?type=${posts.id}`)}>Edit</i>
                            <i className="fa fa-spinner fa-spin" id={posts.id} onClick= {deletePost}>Delete</i>
                            <Button type="button" id={posts.id}  onClick= {deletePost}>Delete</Button>
                            <Button type="button" id={posts.id}  onClick={() => navigate(`/add-blog/?type=${posts.id}`)}>Edit</Button>
                        </div>
                    </div>
                        
                ))}
                   
                </div>
        </div>
        
    ) 
}

export default BlogPage;
